const storeList = {
    pageAtion: [],
    pageEditid: [],
    user: undefined,
    login: false
}

const Reducer = (state = storeList, action) => {
    switch (action.type) {
        case 'userLogin':
            return Object.assign({}, state, {
                user: state.user = action.data,
            })

        case 'logout':
            return Object.assign({}, state, {
                user: state.user = undefined,
            })

        case 'loginstate':
            return Object.assign({}, state, {
                user: state.login = undefined,
            })
        default:
            return state
    }
}

export default Reducer
