import React from 'react'
import { Form, Input, Row, Button, Select, DatePicker } from 'antd'
import { EmployeeRegiterApi, EmployeePrefixApi } from '../../api/EmployeeApis'

const { Option } = Select
const layout = {
    labelCol: { span: 12 },
    wrapperCol: { span: 24 }
}

const tailLayout = {
    wrapperCol: {
        offset: 8,
        span: 16,
    },
}

const config = {
    rules: [
      {
        type: 'object',
        required: true,
        message: 'Please select time!',
      },
    ],
  }

class RegiterComponent extends React.Component {

    constructor() {
        super()
        this.state = {
            prefix: []
        }
    }

    async componentDidMount() {
        let resultPrefix = await EmployeePrefixApi()

        this.setState({
            prefix: resultPrefix.rows
        })
    }

    FormFinish = async (e) => {
        const values = {
            ...e,
            'birthDay': e['birthDay'].format('YYYY-MM-DD')
        }
         let resulutRegiter = await EmployeeRegiterApi(values)
        console.log(resulutRegiter)
    }

    render() {
        return (
            <Row justify="center">
                <div>Create your account</div>
                <Form
                    {...layout}
                    onFinish={this.FormFinish}
                    style={{ width: '400px' }}
                >
                    <Form.Item
                        label="เลขประจำตัวประชาชน"
                        name="cid"
                        rules={[{ required: true, message: 'Please input your cid!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="คำนำ"
                        name="prefix"
                        rules={[{ required: true, message: 'Please input your prefix!' }]}
                    >
                        <Select>
                            {this.state.prefix.map((a, b) =>
                                <Option key={b} value={a.employee_prefix_id}>{a.employee_prefix_description}</Option>
                            )}
                        </Select>
                    </Form.Item>
                    <Form.Item
                        label="ชื่อ"
                        name="fName"
                        rules={[{ required: true, message: 'Please input your firstName!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="นามสกุล"
                        name="lName"
                        rules={[{ required: true, message: 'Please input your lastName!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="วัน/เดือน/ปี (วันเกิด)"
                        name="birthDay"
                        rules={[{ required: true, message: 'Please input your birthDay!' }]}
                        {...config}
                    >
                        <DatePicker

                        />
                    </Form.Item>
                    <Form.Item
                        label="Email"
                        name="email"
                        rules={[{ required: true, message: 'Please input your email!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="เบอร์โทรศัพ"
                        name="phone"
                        rules={[{ required: true, message: 'Please input your phone!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="USER"
                        name="user"
                        rules={[{ required: true, message: 'Please input your user!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="PASSWORD"
                        name="passWord"
                        rules={[{ required: true, message: 'Please input your passWord!' }]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item {...tailLayout} >
                        <Button type="primary" htmlType="submit">
                            {'Submit'}
                        </Button>
                    </Form.Item>
                </Form>
            </Row >
        )
    }
}

export default RegiterComponent
