exports.FetchApi = (url, path, data) => {

    return fetch(url + path, {
        method: 'post',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    })
        .then(res => res.json())
        .then(res => {
            return res
        })
        .catch(err => {
            throw err
        })
}
