const FetchApi = (url, path, data) => {
     fetch(url + path, {
        method: "POST",
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    })
        .then(res => res.json())
        .then(res => {
            return res
        })
        .catch(err => {
            throw err
        })
}

export default FetchApi
