import React from 'react'
import { Form, Input, DatePicker, Select, Button, Col } from 'antd'
import local from 'antd/es/date-picker/locale/th_TH'
import { AnimalDataRegit } from '../../api/AnimalApis'

const { Option } = Select
class AnimalRegiterComponent extends React.Component {

    constructor() {
        super()
        this.state = {
            animalType: [],
            animalSex: []
        }
    }

    async componentDidMount() {
        let dataRegit = await AnimalDataRegit()

        console.log(dataRegit.AnimalType)
        this.setState({
            animalType: dataRegit.AnimalType,
            animalSex: dataRegit.AnimalSex
        })
    }

    onFinish = (e) => {
        let value = {
            ...e,
            'birthDay': e['birthDay'].format('YYYY-MM-DD')
        }
        console.log(value)
    }

    render() {

        return (
            <Col sm={{ span: 24 }}>
                <Form
                    wrapperCol={{
                        span: 11
                    }}
                    labelCol={{
                        span:7
                    }}
                    onFinish={this.onFinish}
                >
                    <Form.Item
                        label="ชนิดสัตว์ป่า"
                        name="animalType"
                    >
                        <Select>
                            {this.state.animalType.map((a, b) =>
                                (
                                    <Option key={b} value={a.animal_type_id}>{a.animal_type_description}</Option>
                                )
                            )}
                        </Select>
                    </Form.Item>
                    <Form.Item
                        label="ชื่อเล่น"
                        name="nickName"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="ชื่อจริง"
                        name="fName"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="นามสกุล"
                        name="lName"
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="วันเกิด"
                        name="birthDay"
                    >
                        <DatePicker
                            locale={local}
                        />
                    </Form.Item>
                    <Form.Item
                        label="เพศ"
                        name="animalSex"
                    >
                        <Select>
                            {this.state.animalSex.map((a, b) =>
                                (
                                    <Option key={b} value={a.animal_sex_id}>{a.animal_sex_description}</Option>
                                )
                            )}
                        </Select>
                    </Form.Item>
                    <Form.Item
                        wrapperCol={{ offset: 4, span: 12 }}
                    >
                        <Button
                            htmlType="submit"
                            type="primary"
                            block
                        >
                            {'บันทึก'}
                        </Button>
                    </Form.Item>
                </Form>
            </Col>
        )

    }
}

export default AnimalRegiterComponent
