import React from 'react'
import { Form, Input, Row, Button } from 'antd'
import { EmployeeRegiterApi } from '../../api/EmployeeRegiterApi'

const layout = {
    labelCol: { span: 12 },
    wrapperCol: { span: 24 }
}
const tailLayout = {
    wrapperCol: {
        offset: 8,
        span: 16,
    },
}

class RegiterComponent extends React.Component {

    constructor() {
        super()
        this.state = {

        }
    }

    FormFinish = async (e) => {
        let resulutRegiter = await EmployeeRegiterApi(e)
        console.log(resulutRegiter)

    }

    render() {
        return (
            <Row justify="center">
                <Form
                    {...layout}
                    onFinish={this.FormFinish}
                    style={{width:'500px'}}
                >
                    <Form.Item
                        label="เลขประจำตัวประชาชน"
                        name="cid"
                        rules={[{ required: true, message: 'Please input your cid!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="คำนำ"
                        name="prefix"
                        rules={[{ required: true, message: 'Please input your prefix!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="ชื่อ"
                        name="fName"
                        rules={[{ required: true, message: 'Please input your firstName!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="นามสกุล"
                        name="lName"
                        rules={[{ required: true, message: 'Please input your lastName!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="วัน/เดือน/ปี (วันเกิด)"
                        name="birthDay"
                        rules={[{ required: true, message: 'Please input your birthDay!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="Email"
                        name="email"
                        rules={[{ required: true, message: 'Please input your email!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="เบอร์โทรศัพ"
                        name="phone"
                        rules={[{ required: true, message: 'Please input your phone!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="USER"
                        name="user"
                        rules={[{ required: true, message: 'Please input your user!' }]}
                    >
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label="PASSWORD"
                        name="passWord"
                        rules={[{ required: true, message: 'Please input your passWord!' }]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item {...tailLayout} >
                        <Button type="primary" htmlType="submit">
                            {'Submit'}
                        </Button>
                    </Form.Item>
                </Form>
            </Row >
        )
    }
}

export default RegiterComponent
